	function toControl(sId, bInvalidate) {
		if (!sId) {
			return null;
		}
		
		var oControl = sap.ui.getCore().byId(sId);
		if (oControl && bInvalidate) {
			oControl.invalidate();
		}
		
		return oControl;
	}
	
